    type User {
        _id: ID
        firstname: String!
        lastname: String
        age: Int
    }

    type Mutation {
        createTask(input: TaskInput): Task
        createUser(input: UserInput): User
        deleteUser(_id: ID): User
        updateUser(_id: ID, input: UserInput): User 
    }

    input TaskInput {
        title: String!
        description: String!
        time: Int
    }

    input UserInput {
        firstname: String!
        lastname: String
        age: Int!
    }
`;

export default makeExecutableSchema({
    typeDefs: typeDefs,
    resolvers: resolvers
})

